{% if collection.handle == 'sunbum-all' or collection.handle == 'moken-all' %}
  <style>
    .circle-subcategories {
      display: flex;
      gap: 12px;
      flex-wrap: wrap;
      justify-content: center;
      margin: 20px 0;
    }

    .circle-subcategory {
      display: flex;
      flex-direction: column;
      align-items: center;
      text-align: center;
      text-decoration: none;
      color: #333;
      font-weight: bold;
      width: 70px;
      font-size: 14px;
    }

    .circle-subcategory img {
      width: 55px;
      height: 55px;
      object-fit: cover;
      border-radius: 50%;
      margin-bottom: 6px;
      border: 1px solid #ccc;
    }

    @media screen and (min-width: 768px) {
      .circle-subcategory {
        width: 120px;
        font-size: 16px;
      }

      .circle-subcategory img {
        width: 110px;
        height: 110px;
      }
    }
  </style>
{% endif %}

{% if collection.handle == 'sunbum-all' %}
  <div class="circle-subcategories">
